import React, { useEffect, useMemo, useRef, useState } from 'react';
import { xamanCreatePayment } from '../services/api';
import { checkUserPaymentStatus } from '../services/userWalletService';
import { isCrossmarkAvailable, connectCrossmark, signRlusdPaymentWithCrossmark } from '../services/crossmarkService';
import QRModal from './common/QRModal';
import { Heart, MessageSquare, Mic, ArrowLeft, ArrowRight, CheckCircle2, Eye, EyeOff, Sparkles, Star, Zap, Globe, Shield, Users, Gift, Send } from 'lucide-react';
import './WhisperFlow.css';

// Helpers
const DRAFT_KEY = 'whisperDraftV1';
const PENDING_KEY = 'whisperPendingDonationV1';
const QUOTES = [
  '"Sometimes a few words can carry a lifetime."',
  '"A small gift can change a whole day."',
  '"Light grows where we speak hope."',
  '"Your words have power beyond measure."',
  '"Every whisper of kindness echoes forever."',
];

const STORY_THEME: Record<string, { accent: string; soft1: string; soft2: string }> = {
  meda: { accent: '#7c3aed', soft1: '#ede9fe', soft2: '#faf5ff' },
  tara: { accent: '#ec4899', soft1: '#ffe4f1', soft2: '#fff7fb' },
  ocean: { accent: '#2563eb', soft1: '#dbeafe', soft2: '#eff6ff' },
};

function saveDraft(data: any) { try { localStorage.setItem(DRAFT_KEY, JSON.stringify(data)); } catch {} }
function loadDraft(): any | null { try { return JSON.parse(localStorage.getItem(DRAFT_KEY) || 'null'); } catch { return null; } }

function getPendingDonation(): any | null { try { return JSON.parse(localStorage.getItem(PENDING_KEY) || 'null'); } catch { return null; } }
function clearPendingDonation() { try { localStorage.removeItem(PENDING_KEY); } catch {} }

function useReducedMotion() {
  const [reduced, setReduced] = useState(false);
  useEffect(() => {
    const m = window.matchMedia('(prefers-reduced-motion: reduce)');
    setReduced(m.matches);
    const handler = (e: MediaQueryListEvent) => setReduced(e.matches);
    if (m.addEventListener) m.addEventListener('change', handler);
    else m.addListener(handler as any);
    return () => {
      if (m.removeEventListener) m.removeEventListener('change', handler);
      else m.removeListener(handler as any);
    };
  }, []);
  return reduced;
}

function vibrate(ms = 10) { try { if (navigator.vibrate) navigator.vibrate(ms); } catch {} }

const WhisperFlow: React.FC = () => {
  const [step, setStep] = useState<number>(0);

  // data
  const [message, setMessage] = useState('');
  const [amount, setAmount] = useState<number | 'other' | ''>('');
  const [otherAmount, setOtherAmount] = useState<string>('');
  const [isPublic, setIsPublic] = useState<boolean>(false);
  const [email, setEmail] = useState<string>('');


  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const [showUndo, setShowUndo] = useState<boolean>(false);
  const undoTimerRef = useRef<number | null>(null);
  const [quoteIdx, setQuoteIdx] = useState(0);
  const [showConfetti, setShowConfetti] = useState(false);
  const [transactionUrl, setTransactionUrl] = useState<string | null>(null);
  const [xamanState, setXamanState] = useState<
    | { phase: 'idle' }
    | { phase: 'creating' }
    | { phase: 'ready'; payloadId: string; qrCode?: string; message?: string }
    | { phase: 'completed'; txid?: string }
    | { phase: 'error'; message: string }
  >({ phase: 'idle' });
  const xamanPollRef = useRef<number | null>(null);
  const [crossmarkBusy, setCrossmarkBusy] = useState(false);
  const [crossmarkError, setCrossmarkError] = useState<string | null>(null);

  const MEDA_ADDRESS = (process.env.REACT_APP_MEDA_WALLET_ADDRESS as string) || 'rEtpfxtsuHGWbRYFGszE5etYNPaiogDpxC';
  const RLUSD_ISSUER = 'rQhWct2fv4Vc4KRjRgMrxa8xPN9Zx9iLKV';

  const reducedMotion = useReducedMotion();

  // Voice
  const [listening, setListening] = useState(false);
  const recognitionRef = useRef<any>(null);

  // Deep link
  const searchParams = useMemo(() => new URLSearchParams(window.location.search), []);
  const storyId = (searchParams.get('story') || '').toLowerCase();
  const prefillAmount = searchParams.get('amount');
  const prefillMessage = searchParams.get('message');

  // Theme
  const theme = STORY_THEME[storyId] || STORY_THEME['meda'];

  useEffect(() => {
    const el = document.querySelector('.whisper') as HTMLElement | null;
    if (el) {
      el.style.setProperty('--accent', theme.accent);
      el.style.setProperty('--soft1', theme.soft1);
      el.style.setProperty('--soft2', theme.soft2);
    }
  }, [theme]);

  // Quote rotation
  useEffect(() => {
    const interval = setInterval(() => {
      setQuoteIdx((prev) => (prev + 1) % QUOTES.length);
    }, 4000);
    return () => clearInterval(interval);
  }, []);

  // Prefill from URL params
  useEffect(() => {
    if (prefillAmount) {
      const num = Number(prefillAmount);
      if (!isNaN(num) && num > 0) {
        setAmount(num);
        setShowUndo(true);
        if (undoTimerRef.current) clearTimeout(undoTimerRef.current);
        undoTimerRef.current = window.setTimeout(() => setShowUndo(false), 3000);
      }
    }
    if (prefillMessage) {
      setMessage(prefillMessage);
    }
  }, [prefillAmount, prefillMessage]);

  // Load draft
  useEffect(() => {
    const draft = loadDraft();
    if (draft && step === 0) {
      setMessage(draft.message || '');
      setAmount(draft.amount || '');
      setOtherAmount(draft.otherAmount || '');
      setIsPublic(draft.isPublic || false);
      setEmail(draft.email || '');
    }
  }, [step]);

  // Save draft
  useEffect(() => {
    if (step > 0) {
      saveDraft({ message, amount, otherAmount, isPublic, email });
    }
  }, [message, amount, otherAmount, isPublic, email, step]);

  // Check for pending donation - only restore if user explicitly continues
  useEffect(() => {
    const pending = getPendingDonation();
    if (pending && pending.step === 4) {
      // Clear pending donation to start fresh
      clearPendingDonation();
    }
  }, []);

  // Online status handling
  useEffect(() => {
    const onOnline = async () => {
      if (step === 4) {
        const pending = getPendingDonation();
        if (pending) {
          try {
            await handleSubmit();
            clearPendingDonation();
          } catch (error) {
            console.error('Failed to retry submission:', error);
          }
        }
      }
    };

    window.addEventListener('online', onOnline);
    return () => window.removeEventListener('online', onOnline);
  }, [step]);

  // Pointer light effect
  useEffect(() => {
    const el = document.querySelector('.story-hero') as HTMLElement | null;
    if (!el || reducedMotion) return;

    const onMove = (e: PointerEvent) => {
      const rect = el.getBoundingClientRect();
      const x = ((e.clientX - rect.left) / rect.width) * 100;
      const y = ((e.clientY - rect.top) / rect.height) * 100;
      el.style.setProperty('--x', `${x}%`);
      el.style.setProperty('--y', `${y}%`);
    };

    el.addEventListener('pointermove', onMove);
    return () => el.removeEventListener('pointermove', onMove);
  }, [reducedMotion]);

  const handleMessageChange = (e: React.ChangeEvent<HTMLTextAreaElement>) => {
    setMessage(e.target.value);
  };



  const selectAmount = (val: number | 'other') => {
    if (val === 'other') {
      setAmount('other');
      setOtherAmount('');
    } else {
      setAmount(val);
      setOtherAmount('');
    }
    setShowUndo(true);
    if (undoTimerRef.current) clearTimeout(undoTimerRef.current);
    undoTimerRef.current = window.setTimeout(() => setShowUndo(false), 3000);
    vibrate();
  };

  const undoAmount = () => { setAmount(''); setShowUndo(false); };
  const handleOtherAmount = (e: React.ChangeEvent<HTMLInputElement>) => setOtherAmount(e.target.value);
  const handleIdentityChoice = (choice: boolean) => { setIsPublic(choice); if (!choice) { setEmail(''); setStep(4); } vibrate(); };
  const proceedFromIdentity = () => { setStep(4); vibrate(); };

  // Online status handling
  useEffect(() => {
    const onOnline = async () => {
      if (step === 4) {
        const pending = getPendingDonation();
        if (pending) {
          try {
            await handleSubmit();
            clearPendingDonation();
          } catch (error) {
            console.error('Failed to retry submission:', error);
          }
        }
      }
    };

    window.addEventListener('online', onOnline);
    return () => window.removeEventListener('online', onOnline);
  }, [step]);

  const handleSubmit = async () => {
    if (submitting) return;

    setSubmitting(true);
    setError(null);

    const finalAmount = amount === 'other' ? Number(otherAmount) : Number(amount);

    if (!finalAmount || finalAmount <= 0) {
      setError('Please enter a valid amount');
      setSubmitting(false);
      return;
    }

    // Use the demo RLUSD flow (1 RLUSD fixed amount)
    await startRlusdXamanFromWhisper();
    setSubmitting(false);
  };

  const startRlusdXamanFromWhisper = async () => {
    console.log('startRlusdXamanFromWhisper called');
    console.log('xamanState.phase:', xamanState.phase);
    if (xamanState.phase !== 'idle') {
      console.log('Xaman not idle, returning');
      return;
    }

    setXamanState({ phase: 'creating' });
    console.log('Set xamanState to creating');

    try {
      console.log('Calling xamanCreatePayment with:', {
        destination: MEDA_ADDRESS,
        amount: 1,
        charity: 'MEDA',
        cause_id: 'whisper_rlusd_demo'
      });
      const response = await xamanCreatePayment({
        destination: MEDA_ADDRESS,
        amount: 1, // Fixed 1 RLUSD like the demo
        charity: 'MEDA',
        cause_id: 'whisper_rlusd_demo'
      });

      console.log('xamanCreatePayment response:', response);

      if (response.success && response.payloadId) {
        console.log('Setting xamanState to ready with payloadId:', response.payloadId);
        setXamanState({ 
          phase: 'ready', 
          payloadId: response.payloadId,
          qrCode: response.qrCode
        });

        // Poll for completion
        xamanPollRef.current = window.setInterval(async () => {
          try {
            const status = await checkUserPaymentStatus(response.payloadId!);
            if (status.completed) {
              if (xamanPollRef.current) clearInterval(xamanPollRef.current);
              setXamanState({ phase: 'completed', txid: status.txid });
              setTransactionUrl(status.transactionUrl);
              setShowConfetti(true);
              setTimeout(() => setShowConfetti(false), 2000);
              setStep(5);
            }
          } catch (error) {
            console.error('Payment status check failed:', error);
          }
        }, 2000);
      } else {
        setXamanState({ phase: 'error', message: 'Failed to create payment' });
      }
    } catch (error) {
      console.error('Xaman payment creation failed:', error);
      setXamanState({ phase: 'error', message: 'Failed to create payment' });
    }
  };

  const startXrpXamanFromWhisper = async () => {
    if (xamanState.phase !== 'idle') return;

    setXamanState({ phase: 'creating' });

    try {
      const finalAmount = amount === 'other' ? Number(otherAmount) : Number(amount);
      const response = await xamanCreatePayment({
        amount: finalAmount,
        destination: MEDA_ADDRESS,
        charity: 'MEDA',
        cause_id: 'whisper',
        asset: 'RLUSD',
        issuer: RLUSD_ISSUER
      });

      if (response.success && response.payloadId) {
        setXamanState({ 
          phase: 'ready', 
          payloadId: response.payloadId,
          qrCode: response.qrCode
        });

        // Poll for completion
        xamanPollRef.current = window.setInterval(async () => {
          try {
            const status = await checkUserPaymentStatus(response.payloadId!);
            if (status.completed) {
              if (xamanPollRef.current) clearInterval(xamanPollRef.current);
              setXamanState({ phase: 'completed', txid: status.txid });
              setTransactionUrl(status.transactionUrl);
              setShowConfetti(true);
              setTimeout(() => setShowConfetti(false), 2000);
              setStep(5);
            }
          } catch (error) {
            console.error('Payment status check failed:', error);
          }
        }, 2000);
      } else {
        setXamanState({ phase: 'error', message: 'Failed to create payment' });
      }
    } catch (error) {
      console.error('Xaman payment creation failed:', error);
      setXamanState({ phase: 'error', message: 'Failed to create payment' });
    }
  };

  const startRlusdCrossmarkFromWhisper = async () => {
    if (crossmarkBusy) return;

    setCrossmarkBusy(true);
    setCrossmarkError(null);

    try {
      if (!isCrossmarkAvailable()) {
        throw new Error('CROSSMARK extension not found');
      }

      await connectCrossmark();
      const finalAmount = amount === 'other' ? Number(otherAmount) : Number(amount);

      const result = await signRlusdPaymentWithCrossmark({
        amount: finalAmount,
        destination: MEDA_ADDRESS,
        charity: 'MEDA',
        causeId: 'whisper'
      });

      if (result.ok) {
        setTransactionUrl(result.explorer || null);
        setShowConfetti(true);
        setTimeout(() => setShowConfetti(false), 2000);
        setStep(5);
      } else {
        setCrossmarkError(result.error || 'Payment failed');
      }
    } catch (error) {
      console.error('CROSSMARK payment failed:', error);
      setCrossmarkError(error instanceof Error ? error.message : 'Payment failed');
    } finally {
      setCrossmarkBusy(false);
    }
  };

  const toggleMic = () => {
    if (listening) {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
      setListening(false);
    } else {
      if ('webkitSpeechRecognition' in window) {
        const recognition = new (window as any).webkitSpeechRecognition();
        recognition.continuous = false;
        recognition.interimResults = false;
        recognition.lang = 'en-US';

        recognition.onstart = () => setListening(true);
        recognition.onresult = (event: any) => {
          const transcript = event.results[0][0].transcript;
          setMessage(transcript);
        };
        recognition.onerror = () => setListening(false);
        recognition.onend = () => setListening(false);

        recognitionRef.current = recognition;
        recognition.start();
      } else {
        alert('Speech recognition not supported in this browser');
      }
    }
  };

  // Confetti pieces
  const pieces = Array.from({ length: 7 }).map((_, i) => ({
    left: `${15 + i * 12}%`,
    bottom: '20px',
    background: i % 2 ? theme.accent : theme.soft1,
  }));

  return (
    <div className={`whisper ${reducedMotion ? 'reduced-motion' : ''}`}>


      {step === 0 && (
        <div className={`stage full ${reducedMotion ? '' : 'fade-in'}`}>
          <div className="story-hero" role="img" aria-label={storyId ? `Story ${storyId}` : 'Story image'}>
            <div className="overlay" />

            {/* Floating decorative elements */}
            <div className="floating-elements">
              {/* Left side icons */}
              <div className="floating-icon" style={{ top: '15%', left: '5%', animationDelay: '0s' }}>
                <Sparkles size={24} color={theme.accent} />
              </div>
              <div className="floating-icon" style={{ top: '35%', left: '10%', animationDelay: '1s' }}>
                <Star size={20} color={theme.soft1} />
              </div>
              <div className="floating-icon" style={{ top: '55%', left: '8%', animationDelay: '2s' }}>
                <Heart size={18} color={theme.accent} />
              </div>
              <div className="floating-icon" style={{ top: '75%', left: '12%', animationDelay: '0.5s' }}>
                <Zap size={22} color={theme.soft1} />
              </div>

              {/* Right side icons */}
              <div className="floating-icon" style={{ top: '25%', right: '8%', animationDelay: '1.5s' }}>
                <Globe size={20} color={theme.accent} />
              </div>
              <div className="floating-icon" style={{ top: '45%', right: '12%', animationDelay: '0.8s' }}>
                <Shield size={19} color={theme.soft1} />
              </div>
              <div className="floating-icon" style={{ top: '65%', right: '6%', animationDelay: '2.2s' }}>
                <Users size={21} color={theme.accent} />
              </div>
              <div className="floating-icon" style={{ top: '85%', right: '10%', animationDelay: '1.2s' }}>
                <Gift size={23} color={theme.soft1} />
              </div>

              {/* Right side icons to fill the space */}
              <div className="floating-icon" style={{ top: '10%', right: '15%', animationDelay: '0.3s' }}>
                <Sparkles size={28} color={theme.accent} />
              </div>
              <div className="floating-icon" style={{ top: '20%', right: '25%', animationDelay: '1.3s' }}>
                <Star size={22} color={theme.soft1} />
              </div>
              <div className="floating-icon" style={{ top: '30%', right: '10%', animationDelay: '2.3s' }}>
                <Heart size={20} color={theme.accent} />
              </div>
              <div className="floating-icon" style={{ top: '40%', right: '20%', animationDelay: '0.8s' }}>
                <Zap size={24} color={theme.soft1} />
              </div>
              <div className="floating-icon" style={{ top: '50%', right: '15%', animationDelay: '1.8s' }}>
                <Globe size={21} color={theme.accent} />
              </div>
              <div className="floating-icon" style={{ top: '60%', right: '25%', animationDelay: '2.8s' }}>
                <Shield size={23} color={theme.soft1} />
              </div>
              <div className="floating-icon" style={{ top: '70%', right: '12%', animationDelay: '1.1s' }}>
                <Users size={25} color={theme.accent} />
              </div>
              <div className="floating-icon" style={{ top: '80%', right: '18%', animationDelay: '2.1s' }}>
                <Gift size={19} color={theme.soft1} />
              </div>
              <div className="floating-icon" style={{ top: '90%', right: '22%', animationDelay: '0.6s' }}>
                <Sparkles size={26} color={theme.accent} />
              </div>
            </div>

            {/* People in need images */}
            <div className="people-images">
              <div className="person-image" style={{ top: '10%', left: '8%', animationDelay: '0.5s' }}>
                <div className="image-placeholder">
                  <Users size={40} color={theme.accent} />
                  <div className="image-caption">Students in need</div>
                </div>
              </div>
              <div className="person-image" style={{ top: '30%', left: '20%', animationDelay: '1.2s' }}>
                <div className="image-placeholder">
                  <Heart size={40} color={theme.accent} />
                  <div className="image-caption">Families seeking help</div>
                </div>
              </div>
              <div className="person-image" style={{ top: '50%', left: '12%', animationDelay: '0.8s' }}>
                <div className="image-placeholder">
                  <Shield size={40} color={theme.accent} />
                  <div className="image-caption">Community support</div>
                </div>
              </div>
              <div className="person-image" style={{ top: '70%', left: '18%', animationDelay: '1.5s' }}>
                <div className="image-placeholder">
                  <Globe size={40} color={theme.accent} />
                  <div className="image-caption">Global impact</div>
                </div>
              </div>
              <div className="person-image" style={{ top: '20%', left: '75%', animationDelay: '0.3s' }}>
                <div className="image-placeholder">
                  <Gift size={40} color={theme.accent} />
                  <div className="image-caption">Your generosity</div>
                </div>
              </div>
              <div className="person-image" style={{ top: '60%', left: '80%', animationDelay: '1.8s' }}>
                <div className="image-placeholder">
                  <Sparkles size={40} color={theme.accent} />
                  <div className="image-caption">Hope & healing</div>
                </div>
              </div>
            </div>

            <div className="hero-content">
               <div 
                 key={quoteIdx} 
                 className={`hero-quote ${reducedMotion ? '' : 'quote-fade'}`}
               >
                 {QUOTES[quoteIdx]}
               </div>
              <button className="cta-button" onClick={() => { setStep(1); vibrate(); }}>
                <Send size={20} />
                Whisper a Message
              </button>
            </div>
          </div>
        </div>
      )}

      {step === 1 && (
        <div className={`stage center ${reducedMotion ? '' : 'fade-in'}`} aria-live="polite">
          <div className="step-header">
            <button className="back-button" onClick={() => { setStep(0); vibrate(); }}>
              <ArrowLeft size={20} />
              Back
            </button>
            <div className="step-indicator">Step 1 of 4</div>
          </div>

          <div className="prompt glow type-on">I'm giving because…</div>
          <div className="input-container">
            <textarea 
              className="whisper-input" 
              placeholder="For girls who never had a laptop." 
              value={message} 
              onChange={handleMessageChange} 
              autoFocus 
            />
            <div className="input-footer">
              <div className="hint">{listening ? 'Listening… speak your message' : 'Pause to continue'}</div>
              <button className="mic-button" onClick={toggleMic} aria-pressed={listening}>
                <Mic size={16} />
                {listening ? 'Stop' : 'Speak'}
              </button>
            </div>
          </div>

          <div className="actions">
            <button 
              className="primary-button" 
              onClick={() => ((message || '').trim().length > 0 ? (setStep(2), vibrate()) : null)} 
              disabled={(message || '').trim().length === 0}
            >
              Next
              <ArrowRight size={16} />
            </button>
          </div>
        </div>
      )}

      {step === 2 && (
        <div className={`stage center ${reducedMotion ? '' : 'fade-in'}`} aria-live="polite">
          <div className="step-header">
            <button className="back-button" onClick={() => { setStep(1); vibrate(); }}>
              <ArrowLeft size={20} />
              Back
            </button>
            <div className="step-indicator">Step 2 of 4</div>
          </div>

          <div className="prompt">How much do you want to give?</div>
          <div className="amount-grid">
            {[10, 25, 50, 100].map((v) => (
              <button key={v} className={`amount-pill ${amount === v ? 'selected' : ''}`} onClick={() => selectAmount(v)}>
                ${v}
              </button>
            ))}
            <button className={`amount-pill ${amount === '' && otherAmount ? 'selected' : ''}`} onClick={() => selectAmount('other')}>
              Other
            </button>
          </div>

          {showUndo && (
            <div className="undo-hint">
              Amount selected. <button className="link-button" onClick={undoAmount}>Undo</button>
            </div>
          )}

          {amount === '' && (
            <div className={`other-amount-row ${reducedMotion ? '' : 'slide-up'}`}>
              <input 
                type="number" 
                inputMode="decimal" 
                min={1} 
                step="0.01" 
                placeholder="Enter amount" 
                className="other-input" 
                value={otherAmount} 
                onChange={handleOtherAmount} 
              />
              <button 
                className="apply-button" 
                onClick={() => (Number(otherAmount) > 0 ? (setAmount(Number(otherAmount)), setStep(3), vibrate()) : null)} 
                disabled={!otherAmount || Number(otherAmount) <= 0}
              >
                Apply
              </button>
            </div>
          )}

          <div className="actions">
            {amount !== '' && (
              <button className="primary-button" onClick={() => { setStep(3); vibrate(); }}>
                Next
                <ArrowRight size={16} />
              </button>
            )}
          </div>
        </div>
      )}

      {step === 3 && (
        <div className={`stage center ${reducedMotion ? '' : 'fade-in'}`} aria-live="polite">
          <div className="step-header">
            <button className="back-button" onClick={() => { setStep(2); vibrate(); }}>
              <ArrowLeft size={20} />
              Back
            </button>
            <div className="step-indicator">Step 3 of 4</div>
          </div>

          <div className="prompt">Would you like the story to know who you are?</div>
          <div className="identity-toggle">
            <button className={`toggle-button ${isPublic ? 'active' : ''}`} onClick={() => handleIdentityChoice(true)}>
              <Eye size={16} />
              Yes, share my name
            </button>
            <button className={`toggle-button ${!isPublic ? 'active' : ''}`} onClick={() => handleIdentityChoice(false)}>
              <EyeOff size={16} />
              No, keep me anonymous
            </button>
          </div>

          {isPublic && (
            <div className={`email-section ${reducedMotion ? '' : 'slide-up'}`}>
              <input 
                type="email" 
                placeholder="you@example.com" 
                className="email-input" 
                value={email} 
                onChange={(e) => setEmail(e.target.value)} 
                aria-label="Email address" 
              />
              <button className="continue-button" onClick={proceedFromIdentity}>
                Continue
                <ArrowRight size={16} />
              </button>
            </div>
          )}

          <div className="privacy-note">We'll only use your email to send your receipt.</div>

          <div className="actions">
            {!isPublic && (
              <button className="primary-button" onClick={proceedFromIdentity}>
                Next
                <ArrowRight size={16} />
              </button>
            )}
          </div>
        </div>
      )}

      {step === 4 && (
        <div className={`stage center ${reducedMotion ? '' : 'fade-in'}`} aria-live="polite">
          <div className="step-header">
            <button className="back-button" onClick={() => { setStep(3); vibrate(); }}>
              <ArrowLeft size={20} />
              Back
            </button>
            <div className="step-indicator">Step 4 of 4</div>
          </div>

          {error && <div className="error-message" role="alert">{error}</div>}

          <div className="summary-card">
            <div className="summary-header">Your Message</div>
                         <blockquote className="message-preview">"{message || ''}"</blockquote>
            <div className="summary-details">
              <div className="summary-item">
                <span>Amount:</span>
                <span className="amount">${amount === 'other' ? otherAmount : amount}</span>
              </div>
              <div className="summary-item">
                <span>Privacy:</span>
                <span>{isPublic ? 'Public' : 'Anonymous'}</span>
              </div>
            </div>
          </div>

          <button 
            className={`cta-button ${submitting ? 'disabled' : ''}`} 
            onClick={() => {
              console.log('Button clicked!');
              handleSubmit();
            }} 
            disabled={submitting}
          >
            {submitting ? (
              <>
                <div className="spinner" />
                Sending…
              </>
            ) : (
              <>
                <Heart size={20} />
                Send my words and support
              </>
            )}
          </button>

          <div className="alternative-options">
            <div className="options-label">Or try these options:</div>
            <div className="options-grid">
              <button className="option-button" onClick={startXrpXamanFromWhisper}>
                <MessageSquare size={16} />
                XRP (Alternative)
              </button>
              <button 
                className="option-button" 
                onClick={startRlusdCrossmarkFromWhisper} 
                disabled={crossmarkBusy}
              >
                {crossmarkBusy ? (
                  <>
                    <div className="spinner" />
                    Opening…
                  </>
                ) : (
                  <>
                    <Heart size={16} />
                    RLUSD (CROSSMARK)
                  </>
                )}
              </button>
            </div>
            {crossmarkError && <div className="error-message" role="alert">{crossmarkError}</div>}
          </div>
        </div>
      )}

      {step === 5 && (
        <div className={`stage center ${reducedMotion ? '' : 'fade-in'}`} aria-live="polite">
          <div className="success-card">
            <div className="success-icon">
              <CheckCircle2 size={48} />
            </div>
            <h1 className="success-title">Your message has been sent.</h1>
            <p className="success-subtitle">And so has your support.</p>
                         <blockquote className="final-message">"{message || ''}"</blockquote>
            {transactionUrl && (
              <a href={transactionUrl} target="_blank" rel="noopener noreferrer" className="transaction-link">
                View your receipt details
              </a>
            )}
            {!transactionUrl && (
              <p className="processing-note">Your transaction is being processed...</p>
            )}
            <button className="home-button" onClick={() => window.location.href = '/'}>
              Return Home
            </button>
          </div>
        </div>
      )}

      {!reducedMotion && showConfetti && (
        <div className="confetti" aria-hidden>
          {pieces.map((p, i) => (
            <div key={i} className="piece" style={{ left: p.left, bottom: p.bottom, background: p.background }} />
          ))}
        </div>
      )}

      <QRModal
        open={xamanState.phase === 'creating' || xamanState.phase === 'ready'}
        title="Donate with RLUSD"
        qrUrl={xamanState.phase === 'ready' ? xamanState.qrCode : undefined}
        openLink={xamanState.phase === 'ready' ? `https://xumm.app/sign/${(xamanState as any).payloadId}` : undefined}
        network="Testnet"
        status={xamanState.phase === 'creating' ? 'Creating RLUSD payment…' : xamanState.phase === 'ready' ? 'Scan to sign in Xaman' : undefined}
        onClose={() => setXamanState({ phase: 'idle' })}
      />
    </div>
  );
};

export default WhisperFlow;
