import React, { useEffect, useMemo, useRef, useState } from 'react';
import { xamanCreatePayment } from '../services/api';
import { checkUserPaymentStatus } from '../services/userWalletService';
import { isCrossmarkAvailable, connectCrossmark, signRlusdPaymentWithCrossmark } from '../services/crossmarkService';
import QRModal from './common/QRModal';
import DonationConfirmation from './DonationConfirmation';
import { Heart, MessageSquare, Mic, ArrowLeft, ArrowRight, CheckCircle2, Eye, EyeOff, Sparkles, Star, Zap, Globe, Shield, Users, Gift, Send, CreditCard, DollarSign } from 'lucide-react';
import './WhisperFlow.css';

// Helpers
const DRAFT_KEY = 'whisperDraftV1';
const PENDING_KEY = 'whisperPendingDonationV1';
const QUOTES = [
  '"Sometimes a few words can carry a lifetime."',
  '"A small gift can change a whole day."',
  '"Light grows where we speak hope."',
  '"Your words have power beyond measure."',
  '"Every whisper of kindness echoes forever."',
];

const STORY_THEME: Record<string, { accent: string; soft1: string; soft2: string }> = {
  meda: { accent: '#7c3aed', soft1: '#ede9fe', soft2: '#faf5ff' },
  tara: { accent: '#ec4899', soft1: '#ffe4f1', soft2: '#fff7fb' },
  ocean: { accent: '#2563eb', soft1: '#dbeafe', soft2: '#eff6ff' },
};

function saveDraft(data: any) { try { localStorage.setItem(DRAFT_KEY, JSON.stringify(data)); } catch {} }
function loadDraft(): any | null { try { return JSON.parse(localStorage.getItem(DRAFT_KEY) || 'null'); } catch { return null; } }

function getPendingDonation(): any | null { try { return JSON.parse(localStorage.getItem(PENDING_KEY) || 'null'); } catch { return null; } }
function clearPendingDonation() { try { localStorage.removeItem(PENDING_KEY); } catch {} }

function useReducedMotion() {
  const [reduced, setReduced] = useState(false);
  useEffect(() => {
    const m = window.matchMedia('(prefers-reduced-motion: reduce)');
    setReduced(m.matches);
    const handler = (e: MediaQueryListEvent) => setReduced(e.matches);
    if (m.addEventListener) m.addEventListener('change', handler);
    else m.addListener(handler as any);
    return () => {
      if (m.removeEventListener) m.removeEventListener('change', handler);
      else m.removeListener(handler as any);
    };
  }, []);
  return reduced;
}

function vibrate(ms = 10) { try { if (navigator.vibrate) navigator.vibrate(ms); } catch {} }

const WhisperFlow: React.FC = () => {
  const [step, setStep] = useState<number>(0);

  // data
  const [message, setMessage] = useState('');
  const [amount, setAmount] = useState<number | 'other' | ''>('');
  const [otherAmount, setOtherAmount] = useState<string>('');
  const [isPublic, setIsPublic] = useState<boolean>(false);
  const [email, setEmail] = useState<string>('');

  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState<string | null>(null);

  const [showUndo, setShowUndo] = useState<boolean>(false);
  const undoTimerRef = useRef<number | null>(null);
  const [quoteIdx, setQuoteIdx] = useState(0);
  const [showConfetti, setShowConfetti] = useState(false);
  const [transactionUrl, setTransactionUrl] = useState<string | null>(null);
  const [xamanState, setXamanState] = useState<
    | { phase: 'idle' }
    | { phase: 'creating' }
    | { phase: 'ready'; payloadId: string; qrCode?: string; message?: string }
    | { phase: 'completed'; txid?: string }
    | { phase: 'error'; message: string }
  >({ phase: 'idle' });
  const [countdown, setCountdown] = useState<number>(0);
  const xamanPollRef = useRef<number | null>(null);
  const [crossmarkBusy, setCrossmarkBusy] = useState(false);
  const [crossmarkError, setCrossmarkError] = useState<string | null>(null);

  // Fiat transaction state
  const [fiatState, setFiatState] = useState<
    | { phase: 'idle' }
    | { phase: 'processing' }
    | { phase: 'completed'; txid?: string }
    | { phase: 'error'; message: string }
  >({ phase: 'idle' });

  const MEDA_ADDRESS = (process.env.REACT_APP_MEDA_WALLET_ADDRESS as string) || 'rEtpfxtsuHGWbRYFGszE5etYNPaiogDpxC';
  const RLUSD_ISSUER = 'rQhWct2fv4Vc4KRjRgMrxa8xPN9Zx9iLKV';

  const reducedMotion = useReducedMotion();

  // Voice
  const [listening, setListening] = useState(false);
  const recognitionRef = useRef<any>(null);

  // Deep link
  const searchParams = useMemo(() => new URLSearchParams(window.location.search), []);
  const storyId = (searchParams.get('story') || '').toLowerCase();
  const prefillAmount = searchParams.get('amount');
  const prefillMessage = searchParams.get('message');

  // Theme
  const theme = STORY_THEME[storyId] || STORY_THEME['meda'];

  useEffect(() => {
    // Load draft if exists
    const draft = loadDraft();
    if (draft) {
      setMessage(draft.message || '');
      setAmount(draft.amount || '');
      setOtherAmount(draft.otherAmount || '');
      setIsPublic(draft.isPublic || false);
      setEmail(draft.email || '');
    }

    // Check for pending donation
    const pending = getPendingDonation();
    if (pending) {
      setMessage(pending.message || '');
      setAmount(pending.amount || '');
      setOtherAmount(pending.otherAmount || '');
      setIsPublic(pending.isPublic || false);
      setEmail(pending.email || '');
      setStep(4);
    }

    // Prefill from URL params
    if (prefillAmount) {
      const numAmount = Number(prefillAmount);
      if (numAmount > 0) {
        setAmount(numAmount);
      }
    }
    if (prefillMessage) {
      setMessage(prefillMessage);
    }

    // Auto-advance if we have prefill data
    if (prefillAmount || prefillMessage) {
      setStep(1);
    }
  }, [prefillAmount, prefillMessage]);

  // Save draft on changes
  useEffect(() => {
    if (step > 0) {
      saveDraft({
        message,
        amount,
        otherAmount,
        isPublic,
        email,
      });
    }
  }, [message, amount, otherAmount, isPublic, email, step]);

  // Auto-advance quote
  useEffect(() => {
    if (step === 0) {
      const interval = setInterval(() => {
        setQuoteIdx(prev => (prev + 1) % QUOTES.length);
      }, 4000);
      return () => clearInterval(interval);
    }
  }, [step]);

  // Voice recognition setup
  useEffect(() => {
    if ('webkitSpeechRecognition' in window || 'SpeechRecognition' in window) {
      const SpeechRecognition = (window as any).webkitSpeechRecognition || (window as any).SpeechRecognition;
      recognitionRef.current = new SpeechRecognition();
      recognitionRef.current.continuous = false;
      recognitionRef.current.interimResults = false;
      recognitionRef.current.lang = 'en-US';

      recognitionRef.current.onresult = (event: any) => {
        const transcript = event.results[0][0].transcript;
        setMessage(prev => prev + ' ' + transcript);
        setListening(false);
        vibrate();
      };

      recognitionRef.current.onerror = (event: any) => {
        console.error('Speech recognition error:', event.error);
        setListening(false);
      };

      recognitionRef.current.onend = () => {
        setListening(false);
      };
    }
  }, []);

  const startListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.start();
      setListening(true);
      vibrate();
    }
  };

  const stopListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
      setListening(false);
    }
  };

  const handleSubmit = () => {
    if (!message.trim()) {
      setError('Please enter your message');
      return;
    }
    if (!amount && !otherAmount) {
      setError('Please select an amount');
      return;
    }
    setError(null);
    setStep(4);
  };

  const handleUndo = () => {
    setStep(3);
    setShowUndo(false);
    if (undoTimerRef.current) {
      clearTimeout(undoTimerRef.current);
      undoTimerRef.current = null;
    }
  };

  const startRlusdXamanFromWhisper = async () => {
    try {
      setXamanState({ phase: 'creating' });

      const response = await xamanCreatePayment({
        destination: MEDA_ADDRESS,
        amount: 1,
        charity: 'MEDA',
        cause_id: 'whisper_rlusd',
        asset: 'RLUSD',
        issuer: RLUSD_ISSUER
      });

      if (response.success && response.payloadId) {
        setXamanState({ 
          phase: 'ready', 
          payloadId: response.payloadId,
          qrCode: response.qrCode
        });

        // Auto-confirmation for demo (10 seconds)
        setCountdown(10);
        const countdownInterval = setInterval(() => {
          setCountdown(prev => {
            if (prev <= 1) {
              clearInterval(countdownInterval);
              setXamanState({ 
                phase: 'completed', 
                txid: 'demo-tx-' + Date.now().toString().slice(-8)
              });
              setTransactionUrl('https://testnet.xrpl.org/transactions/demo-tx-' + Date.now().toString().slice(-8));
              setShowConfetti(true);
              setTimeout(() => setShowConfetti(false), 2000);
              setStep(5);
              return 0;
            }
            return prev - 1;
          });
        }, 1000);
      } else {
        setXamanState({ phase: 'error', message: 'Failed to create payment' });
      }
    } catch (error) {
      console.error('Error creating Xaman payment:', error);
      setXamanState({ phase: 'error', message: 'Failed to create payment' });
    }
  };

  const startRlusdCrossmarkFromWhisper = async () => {
    try {
      setCrossmarkBusy(true);
      setCrossmarkError(null);

      if (!isCrossmarkAvailable()) {
        setCrossmarkError('Crossmark wallet not available. Please install Crossmark extension.');
        return;
      }

      await connectCrossmark();
      const txid = await signRlusdPaymentWithCrossmark({
        destination: MEDA_ADDRESS,
        amount: 1,
        charity: 'MEDA',
        causeId: 'whisper_rlusd',
        issuer: RLUSD_ISSUER
      });

      if (txid) {
        setTransactionUrl(`https://testnet.xrpl.org/transactions/${txid}`);
        setShowConfetti(true);
        setTimeout(() => setShowConfetti(false), 2000);
        setStep(5);
      } else {
        setCrossmarkError('Payment failed. Please try again.');
      }
    } catch (error) {
      console.error('Crossmark payment error:', error);
      setCrossmarkError('Payment failed. Please try again.');
    } finally {
      setCrossmarkBusy(false);
    }
  };

  const startFiatTransaction = async () => {
    try {
      setFiatState({ phase: 'processing' });

      // Simulate fiat transaction processing
      await new Promise(resolve => setTimeout(resolve, 2000));

      const txid = 'fiat-tx-' + Date.now().toString().slice(-8);
      setFiatState({ phase: 'completed', txid });
      setTransactionUrl(`https://testnet.xrpl.org/transactions/${txid}`);
      setShowConfetti(true);
      setTimeout(() => setShowConfetti(false), 2000);
      setStep(5);
    } catch (error) {
      console.error('Fiat transaction error:', error);
      setFiatState({ phase: 'error', message: 'Fiat transaction failed. Please try again.' });
    }
  };

  const pieces = useMemo(() => {
    return Array.from({ length: 50 }, (_, i) => ({
      left: Math.random() * 100,
      bottom: Math.random() * 100,
      background: ['#ff6b6b', '#4ecdc4', '#45b7d1', '#96ceb4', '#ffeaa7', '#dda0dd', '#98d8c8'][Math.floor(Math.random() * 7)]
    }));
  }, []);

  if (step === 0) {
    return (
      <div className="whisper-flow" style={{ background: `linear-gradient(135deg, ${theme.soft1} 0%, ${theme.soft2} 100%)` }}>
        <div className="hero-content">
          <div className="hero-quote">
            <h1>{QUOTES[quoteIdx]}</h1>
            <p>Share your story and make a difference</p>
          </div>
          <button 
            className="cta-button"
            onClick={() => setStep(1)}
            style={{ background: `linear-gradient(135deg, ${theme.accent} 0%, ${theme.accent}dd 100%)` }}
          >
            <Send size={20} />
            Whisper a Message
          </button>
        </div>

        {/* Floating elements */}
        <div className="floating-elements">
          <div className="floating-icon" style={{ left: '10%', top: '20%', animationDelay: '0s' }}>
            <Sparkles size={24} style={{ color: theme.accent }} />
          </div>
          <div className="floating-icon" style={{ left: '85%', top: '15%', animationDelay: '1s' }}>
            <Star size={20} style={{ color: theme.accent }} />
          </div>
          <div className="floating-icon" style={{ left: '15%', top: '70%', animationDelay: '2s' }}>
            <Zap size={22} style={{ color: theme.accent }} />
          </div>
          <div className="floating-icon" style={{ left: '80%', top: '75%', animationDelay: '3s' }}>
            <Globe size={18} style={{ color: theme.accent }} />
          </div>
          <div className="floating-icon" style={{ left: '90%', top: '50%', animationDelay: '4s' }}>
            <Shield size={16} style={{ color: theme.accent }} />
          </div>
        </div>

        {/* People in need images */}
        <div className="people-images">
          <div className="person-image" style={{ left: '5%', top: '30%', animationDelay: '0.5s' }}>
            <Users size={32} style={{ color: theme.accent }} />
          </div>
          <div className="person-image" style={{ left: '75%', top: '25%', animationDelay: '1.5s' }}>
            <Heart size={28} style={{ color: theme.accent }} />
          </div>
          <div className="person-image" style={{ left: '20%', top: '80%', animationDelay: '2.5s' }}>
            <Gift size={30} style={{ color: theme.accent }} />
          </div>
        </div>
      </div>
    );
  }

  if (step === 1) {
    return (
      <div className="whisper-flow">
        <div className="step-container">
          <div className="step-header">
            <button className="back-button" onClick={() => setStep(0)}>
              <ArrowLeft size={20} />
            </button>
            <div className="step-indicator">Step 1 of 4</div>
          </div>

          <div className="step-content">
            <h2>What's your story?</h2>
            <p>Share a message that will inspire others to give</p>

            <div className="message-input-container">
              <textarea
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Tell us why this cause matters to you..."
                className="message-input"
                rows={6}
              />
              <div className="input-actions">
                <button 
                  className={`voice-button ${listening ? 'listening' : ''}`}
                  onClick={listening ? stopListening : startListening}
                  disabled={!recognitionRef.current}
                >
                  <Mic size={16} />
                  {listening ? 'Listening...' : 'Voice'}
                </button>
                <div className="char-count">{message.length}/500</div>
              </div>
            </div>

            {error && <div className="error-message" role="alert">{error}</div>}

            <button 
              className="next-button"
              onClick={() => setStep(2)}
              disabled={!message.trim()}
            >
              Continue
              <ArrowRight size={16} />
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (step === 2) {
    return (
      <div className="whisper-flow">
        <div className="step-container">
          <div className="step-header">
            <button className="back-button" onClick={() => setStep(1)}>
              <ArrowLeft size={20} />
            </button>
            <div className="step-indicator">Step 2 of 4</div>
          </div>

          <div className="step-content">
            <h2>Choose your amount</h2>
            <p>Select how much you'd like to donate</p>

            <div className="amount-options">
              {[5, 10, 25, 50, 100].map((amt) => (
                <button
                  key={amt}
                  className={`amount-option ${amount === amt ? 'selected' : ''}`}
                  onClick={() => setAmount(amt)}
                >
                  ${amt}
                </button>
              ))}
              <button
                className={`amount-option ${amount === 'other' ? 'selected' : ''}`}
                onClick={() => setAmount('other')}
              >
                Other
              </button>
            </div>

            {amount === 'other' && (
              <div className="other-amount-container">
                <label htmlFor="other-amount">Enter amount:</label>
                <input
                  id="other-amount"
                  type="number"
                  value={otherAmount}
                  onChange={(e) => setOtherAmount(e.target.value)}
                  placeholder="0.00"
                  min="0.01"
                  step="0.01"
                  className="other-amount-input"
                />
              </div>
            )}

            {error && <div className="error-message" role="alert">{error}</div>}

            <button 
              className="next-button"
              onClick={() => setStep(3)}
              disabled={!amount && !otherAmount}
            >
              Continue
              <ArrowRight size={16} />
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (step === 3) {
    return (
      <div className="whisper-flow">
        <div className="step-container">
          <div className="step-header">
            <button className="back-button" onClick={() => setStep(2)}>
              <ArrowLeft size={20} />
            </button>
            <div className="step-indicator">Step 3 of 4</div>
          </div>

          <div className="step-content">
            <h2>Review your donation</h2>
            <p>Make sure everything looks right before you give</p>

            <div className="review-card">
              <div className="review-message">
                <h3>Your Message</h3>
                <p>"{message}"</p>
              </div>

              <div className="review-amount">
                <h3>Amount</h3>
                <p>${amount === 'other' ? otherAmount : amount}</p>
              </div>

              <div className="review-visibility">
                <h3>Visibility</h3>
                <div className="visibility-toggle">
                  <button
                    className={`toggle-option ${isPublic ? 'selected' : ''}`}
                    onClick={() => setIsPublic(true)}
                  >
                    <Eye size={16} />
                    Public
                  </button>
                  <button
                    className={`toggle-option ${!isPublic ? 'selected' : ''}`}
                    onClick={() => setIsPublic(false)}
                  >
                    <EyeOff size={16} />
                    Anonymous
                  </button>
                </div>
              </div>

              {isPublic && (
                <div className="email-input">
                  <label htmlFor="email">Email (optional, for receipt)</label>
                  <input
                    id="email"
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="your@email.com"
                    className="email-field"
                  />
                </div>
              )}
            </div>

            {error && <div className="error-message" role="alert">{error}</div>}

            <button 
              className="next-button"
              onClick={handleSubmit}
            >
              Send my words and support
              <Send size={16} />
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (step === 4) {
    return (
      <div className="whisper-flow">
        <div className="step-container">
          <div className="step-header">
            <button className="back-button" onClick={() => setStep(3)}>
              <ArrowLeft size={20} />
            </button>
            <div className="step-indicator">Step 4 of 4</div>
          </div>

          <div className="step-content">
            <h2>Choose payment method</h2>
            <p>Select how you'd like to complete your donation</p>

            <div className="payment-options">
              <button 
                className="option-button primary" 
                onClick={startRlusdXamanFromWhisper}
              >
                <Heart size={16} />
                RLUSD (XAMAN)
              </button>

              <button 
                className="option-button" 
                onClick={startRlusdCrossmarkFromWhisper} 
                disabled={crossmarkBusy}
              >
                {crossmarkBusy ? (
                  <>
                    <div className="spinner" />
                    Opening…
                  </>
                ) : (
                  <>
                    <Heart size={16} />
                    RLUSD (CROSSMARK)
                  </>
                )}
              </button>

              <button 
                className="option-button" 
                onClick={startFiatTransaction}
                disabled={fiatState.phase === 'processing'}
              >
                {fiatState.phase === 'processing' ? (
                  <>
                    <div className="spinner" />
                    Processing…
                  </>
                ) : (
                  <>
                    <CreditCard size={16} />
                    Fiat Payment (USD)
                  </>
                )}
              </button>
            </div>

            {crossmarkError && <div className="error-message" role="alert">{crossmarkError}</div>}
            {fiatState.phase === 'error' && <div className="error-message" role="alert">{fiatState.message}</div>}
          </div>
        </div>
      </div>
    );
  }

  if (step === 5) {
    return (
      <DonationConfirmation
        message={message}
        amount={amount === 'other' ? Number(otherAmount) : Number(amount)}
        transactionHash={xamanState.phase === 'completed' ? xamanState.txid : fiatState.phase === 'completed' ? fiatState.txid : undefined}
        transactionUrl={transactionUrl || undefined}
        isPublic={isPublic}
        onReturnHome={() => window.location.href = '/'}
      />
    );
  }

  return null;
};

export default WhisperFlow;
