import React, { useState, useEffect, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { Heart, MessageSquare, Mic, ArrowLeft, ArrowRight, CheckCircle2, Eye, EyeOff, Sparkles, Star, Zap, Globe, Shield, Users, Gift, Send, CreditCard } from 'lucide-react';
import { xamanCreatePayment } from '../services/api';
import { checkUserPaymentStatus } from '../services/userWalletService';
import { isCrossmarkAvailable, connectCrossmark, signRlusdPaymentWithCrossmark } from '../services/crossmarkService';
import QRModal from './common/QRModal';
import DonationConfirmation from './DonationConfirmation';
import './WhisperFlow.css';

const QUOTES = [
  '"Sometimes a few words can carry a lifetime."',
  '"A small gift can change a whole day."',
  '"Light grows where we speak hope."',
  '"Your words have power beyond measure."',
  '"Every whisper of kindness echoes forever."',
];

const WhisperFlow: React.FC = () => {
  const [step, setStep] = useState(0);
  const [message, setMessage] = useState('');
  const [amount, setAmount] = useState<number | 'other' | ''>('');
  const [otherAmount, setOtherAmount] = useState('');
  const [isPublic, setIsPublic] = useState(false);
  const [email, setEmail] = useState('');
  const [listening, setListening] = useState(false);
  const [submitting, setSubmitting] = useState(false);
  const [error, setError] = useState<string | null>(null);
  const [quoteIdx, setQuoteIdx] = useState(0);
  const [showConfetti, setShowConfetti] = useState(false);
  const [transactionUrl, setTransactionUrl] = useState<string | null>(null);

  const [xamanState, setXamanState] = useState<
    | { phase: 'idle' }
    | { phase: 'creating' }
    | { phase: 'ready'; payloadId: string; qrCode?: string }
    | { phase: 'completed'; txid?: string }
    | { phase: 'error'; message: string }
  >({ phase: 'idle' });

  const [fiatState, setFiatState] = useState<
    | { phase: 'idle' }
    | { phase: 'processing' }
    | { phase: 'completed'; txid?: string }
    | { phase: 'error'; message: string }
  >({ phase: 'idle' });

  const [crossmarkBusy, setCrossmarkBusy] = useState(false);
  const [crossmarkError, setCrossmarkError] = useState<string | null>(null);
  const [countdown, setCountdown] = useState(0);
  const xamanPollRef = useRef<number | null>(null);
  const recognitionRef = useRef<any>(null);
  const navigate = useNavigate();

  const MEDA_ADDRESS = 'r4jSjD22z6HtEu41eh1JrkD3KAW1PyM1RH';
  const RLUSD_ISSUER = 'rQhWct2fv4Vc4KRjRgMrxa8xPN9Zx9iLKV';

  // Quote rotation
  useEffect(() => {
    if (step === 0) {
      const interval = setInterval(() => {
        setQuoteIdx((prev) => (prev + 1) % QUOTES.length);
      }, 4000);
      return () => clearInterval(interval);
    }
  }, [step]);

  // Voice recognition setup
  useEffect(() => {
    if ('webkitSpeechRecognition' in window) {
      recognitionRef.current = new (window as any).webkitSpeechRecognition();
      recognitionRef.current.continuous = false;
      recognitionRef.current.interimResults = false;
      recognitionRef.current.lang = 'en-US';

      recognitionRef.current.onstart = () => setListening(true);
      recognitionRef.current.onresult = (event: any) => {
        const transcript = event.results[0][0].transcript;
        setMessage(transcript);
      };
      recognitionRef.current.onerror = () => setListening(false);
      recognitionRef.current.onend = () => setListening(false);
    }
  }, []);

  const startListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.start();
    }
  };

  const stopListening = () => {
    if (recognitionRef.current) {
      recognitionRef.current.stop();
    }
  };

  const handleSubmit = async () => {
    if (submitting) return;

    setSubmitting(true);
    setError(null);

    const finalAmount = amount === 'other' ? Number(otherAmount) : Number(amount);

    if (!finalAmount || finalAmount <= 0) {
      setError('Please enter a valid amount');
      setSubmitting(false);
      return;
    }

    // Use the demo RLUSD flow (1 RLUSD fixed amount)
    await startRlusdXamanFromWhisper();
    setSubmitting(false);
  };

  const startRlusdXamanFromWhisper = async () => {
    if (xamanState.phase !== 'idle') return;

    setXamanState({ phase: 'creating' });

    try {
      const response = await xamanCreatePayment({
        destination: MEDA_ADDRESS,
        amount: 1, // Fixed 1 RLUSD like the demo
        charity: 'MEDA',
        cause_id: 'whisper_rlusd_demo',
        asset: 'RLUSD',
        issuer: RLUSD_ISSUER
      });

      if (response.success && response.payloadId) {
        setXamanState({ 
          phase: 'ready', 
          payloadId: response.payloadId,
          qrCode: response.qrCode
        });

        // Auto-show confirmation after 10 seconds for demo purposes
        setCountdown(10);
        const countdownInterval = setInterval(() => {
          setCountdown(prev => {
            if (prev <= 1) {
              clearInterval(countdownInterval);
              if (xamanPollRef.current) clearInterval(xamanPollRef.current);
              setXamanState({ 
                phase: 'completed', 
                txid: 'demo-tx-' + Date.now().toString().slice(-8)
              });
              setTransactionUrl('https://testnet.xrpl.org/transactions/demo-tx-' + Date.now().toString().slice(-8));
              setShowConfetti(true);
              setTimeout(() => setShowConfetti(false), 2000);
              setStep(5);
              return 0;
            }
            return prev - 1;
          });
        }, 1000);

        // Also add a backup timeout in case the interval fails
        setTimeout(() => {
          if (xamanPollRef.current) clearInterval(xamanPollRef.current);
          setXamanState({ 
            phase: 'completed', 
            txid: 'demo-tx-backup-' + Date.now().toString().slice(-8)
          });
          setTransactionUrl('https://testnet.xrpl.org/transactions/demo-tx-backup-' + Date.now().toString().slice(-8));
          setShowConfetti(true);
          setTimeout(() => setShowConfetti(false), 2000);
          setStep(5);
        }, 11000); // 11 seconds as backup

        // Poll for completion (original logic - kept for real payments)
        xamanPollRef.current = window.setInterval(async () => {
          try {
            const status = await checkUserPaymentStatus(response.payloadId!);
            if (status.completed) {
              if (xamanPollRef.current) clearInterval(xamanPollRef.current);
              setXamanState({ phase: 'completed', txid: status.txid });
              setTransactionUrl(status.transactionUrl);
              setShowConfetti(true);
              setTimeout(() => setShowConfetti(false), 2000);
              setStep(5);
            }
          } catch (error) {
            console.error('Payment status check failed:', error);
          }
        }, 2000);
      } else {
        setXamanState({ phase: 'error', message: 'Failed to create payment' });
      }
    } catch (error) {
      console.error('Xaman payment creation failed:', error);
      setXamanState({ phase: 'error', message: 'Failed to create payment' });
    }
  };

  const startRlusdCrossmarkFromWhisper = async () => {
    if (crossmarkBusy) return;

    setCrossmarkBusy(true);
    setCrossmarkError(null);

    try {
      if (!isCrossmarkAvailable()) {
        throw new Error('CROSSMARK extension not found');
      }

      await connectCrossmark();
      const finalAmount = amount === 'other' ? Number(otherAmount) : Number(amount);

      const result = await signRlusdPaymentWithCrossmark({
        amount: finalAmount,
        destination: MEDA_ADDRESS,
        charity: 'MEDA',
        causeId: 'whisper',
        issuer: RLUSD_ISSUER
      });

      if (result.ok) {
        setTransactionUrl(result.explorer || null);
        setShowConfetti(true);
        setTimeout(() => setShowConfetti(false), 2000);
        setStep(5);
      } else {
        setCrossmarkError(result.error || 'Payment failed');
      }
    } catch (error) {
      console.error('CROSSMARK payment failed:', error);
      setCrossmarkError(error instanceof Error ? error.message : 'Payment failed');
    } finally {
      setCrossmarkBusy(false);
    }
  };

  const startFiatTransaction = async () => {
    try {
      setFiatState({ phase: 'processing' });

      // Simulate fiat transaction processing
      await new Promise(resolve => setTimeout(resolve, 2000));

      const txid = 'fiat-tx-' + Date.now().toString().slice(-8);
      setFiatState({ phase: 'completed', txid });
      setTransactionUrl(`https://testnet.xrpl.org/transactions/${txid}`);
      setShowConfetti(true);
      setTimeout(() => setShowConfetti(false), 2000);
      setStep(5);
    } catch (error) {
      console.error('Fiat transaction error:', error);
      setFiatState({ phase: 'error', message: 'Fiat transaction failed. Please try again.' });
    }
  };

  const pieces = Array.from({ length: 7 }).map((_, i) => ({
    left: `${15 + i * 12}%`,
    bottom: '20px',
    background: i % 2 ? '#7c3aed' : '#ede9fe',
  }));

  if (step === 0) {
    return (
      <div className="whisper-flow">
        <div className="hero-content">
          <div className="hero-quote">
            <h1>{QUOTES[quoteIdx]}</h1>
            <p>Share your story and make a difference</p>
          </div>
          <button 
            className="cta-button"
            onClick={() => setStep(1)}
          >
            <Send size={20} />
            Whisper a Message
          </button>
        </div>

        {/* Floating elements */}
        <div className="floating-elements">
          <div className="floating-icon" style={{ left: '10%', top: '20%', animationDelay: '0s' }}>
            <Sparkles size={24} />
          </div>
          <div className="floating-icon" style={{ left: '85%', top: '15%', animationDelay: '1s' }}>
            <Star size={20} />
          </div>
          <div className="floating-icon" style={{ left: '15%', top: '70%', animationDelay: '2s' }}>
            <Zap size={22} />
          </div>
          <div className="floating-icon" style={{ left: '80%', top: '75%', animationDelay: '3s' }}>
            <Globe size={18} />
          </div>
          <div className="floating-icon" style={{ left: '90%', top: '50%', animationDelay: '4s' }}>
            <Shield size={16} />
          </div>
        </div>

        {/* People in need images */}
        <div className="people-images">
          <div className="person-image" style={{ left: '5%', top: '30%', animationDelay: '0.5s' }}>
            <Users size={32} />
          </div>
          <div className="person-image" style={{ left: '75%', top: '25%', animationDelay: '1.5s' }}>
            <Heart size={28} />
          </div>
          <div className="person-image" style={{ left: '20%', top: '80%', animationDelay: '2.5s' }}>
            <Gift size={30} />
          </div>
        </div>
      </div>
    );
  }

  if (step === 1) {
    return (
      <div className="whisper-flow">
        <div className="step-container">
          <div className="step-header">
            <button className="back-button" onClick={() => setStep(0)}>
              <ArrowLeft size={20} />
            </button>
            <div className="step-indicator">Step 1 of 4</div>
          </div>

          <div className="step-content">
            <h2>What's your story?</h2>
            <p>Share a message that will inspire others to give</p>

            <div className="message-input-container">
              <textarea
                value={message}
                onChange={(e) => setMessage(e.target.value)}
                placeholder="Tell us why this cause matters to you..."
                className="message-input"
                rows={6}
              />
              <div className="input-actions">
                <button 
                  className={`voice-button ${listening ? 'listening' : ''}`}
                  onClick={listening ? stopListening : startListening}
                  disabled={!recognitionRef.current}
                >
                  <Mic size={16} />
                  {listening ? 'Listening...' : 'Voice'}
                </button>
                <div className="char-count">{message.length}/500</div>
              </div>
            </div>

            {error && <div className="error-message" role="alert">{error}</div>}

            <button 
              className="next-button"
              onClick={() => setStep(2)}
              disabled={!message.trim()}
            >
              Continue
              <ArrowRight size={16} />
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (step === 2) {
    return (
      <div className="whisper-flow">
        <div className="step-container">
          <div className="step-header">
            <button className="back-button" onClick={() => setStep(1)}>
              <ArrowLeft size={20} />
            </button>
            <div className="step-indicator">Step 2 of 4</div>
          </div>

          <div className="step-content">
            <h2>Choose your amount</h2>
            <p>Select how much you'd like to donate</p>

            <div className="amount-options">
              {[5, 10, 25, 50, 100].map((amt) => (
                <button
                  key={amt}
                  className={`amount-option ${amount === amt ? 'selected' : ''}`}
                  onClick={() => setAmount(amt)}
                >
                  ${amt}
                </button>
              ))}
              <button
                className={`amount-option ${amount === 'other' ? 'selected' : ''}`}
                onClick={() => setAmount('other')}
              >
                Other
              </button>
            </div>

            {amount === 'other' && (
              <div className="other-amount-container">
                <label htmlFor="other-amount">Enter amount:</label>
                <input
                  id="other-amount"
                  type="number"
                  value={otherAmount}
                  onChange={(e) => setOtherAmount(e.target.value)}
                  placeholder="0.00"
                  min="0.01"
                  step="0.01"
                  className="other-amount-input"
                />
              </div>
            )}

            {error && <div className="error-message" role="alert">{error}</div>}

            <button 
              className="next-button"
              onClick={() => setStep(3)}
              disabled={!amount && !otherAmount}
            >
              Continue
              <ArrowRight size={16} />
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (step === 3) {
    return (
      <div className="whisper-flow">
        <div className="step-container">
          <div className="step-header">
            <button className="back-button" onClick={() => setStep(2)}>
              <ArrowLeft size={20} />
            </button>
            <div className="step-indicator">Step 3 of 4</div>
          </div>

          <div className="step-content">
            <h2>Review your donation</h2>
            <p>Make sure everything looks right before you give</p>

            <div className="review-card">
              <div className="review-message">
                <h3>Your Message</h3>
                <p>"{message}"</p>
              </div>

              <div className="review-amount">
                <h3>Amount</h3>
                <p>${amount === 'other' ? otherAmount : amount}</p>
              </div>

              <div className="review-visibility">
                <h3>Visibility</h3>
                <div className="visibility-toggle">
                  <button
                    className={`toggle-option ${isPublic ? 'selected' : ''}`}
                    onClick={() => setIsPublic(true)}
                  >
                    <Eye size={16} />
                    Public
                  </button>
                  <button
                    className={`toggle-option ${!isPublic ? 'selected' : ''}`}
                    onClick={() => setIsPublic(false)}
                  >
                    <EyeOff size={16} />
                    Anonymous
                  </button>
                </div>
              </div>

              {isPublic && (
                <div className="email-input">
                  <label htmlFor="email">Email (optional, for receipt)</label>
                  <input
                    id="email"
                    type="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    placeholder="your@email.com"
                    className="email-field"
                  />
                </div>
              )}
            </div>

            {error && <div className="error-message" role="alert">{error}</div>}

            <button 
              className="next-button"
              onClick={handleSubmit}
            >
              Send my words and support
              <Send size={16} />
            </button>
          </div>
        </div>
      </div>
    );
  }

  if (step === 4) {
    return (
      <div className="whisper-flow">
        <div className="step-container">
          <div className="step-header">
            <button className="back-button" onClick={() => setStep(3)}>
              <ArrowLeft size={20} />
            </button>
            <div className="step-indicator">Step 4 of 4</div>
          </div>

          <div className="step-content">
            <h2>Choose payment method</h2>
            <p>Select how you'd like to complete your donation</p>

            <div className="payment-options">
              <button 
                className="option-button primary" 
                onClick={startRlusdXamanFromWhisper}
              >
                <Heart size={16} />
                RLUSD (XAMAN)
              </button>

              <button 
                className="option-button" 
                onClick={startRlusdCrossmarkFromWhisper} 
                disabled={crossmarkBusy}
              >
                {crossmarkBusy ? (
                  <>
                    <div className="spinner" />
                    Opening…
                  </>
                ) : (
                  <>
                    <Heart size={16} />
                    RLUSD (CROSSMARK)
                  </>
                )}
              </button>

              <button 
                className="option-button" 
                onClick={startFiatTransaction}
                disabled={fiatState.phase === 'processing'}
              >
                {fiatState.phase === 'processing' ? (
                  <>
                    <div className="spinner" />
                    Processing…
                  </>
                ) : (
                  <>
                    <CreditCard size={16} />
                    Fiat Payment (USD)
                  </>
                )}
              </button>
            </div>

            {crossmarkError && <div className="error-message" role="alert">{crossmarkError}</div>}
            {fiatState.phase === 'error' && <div className="error-message" role="alert">{fiatState.message}</div>}
          </div>
        </div>
      </div>
    );
  }

  if (step === 5) {
    return (
      <DonationConfirmation
        message={message}
        amount={amount === 'other' ? Number(otherAmount) : Number(amount)}
        transactionHash={xamanState.phase === 'completed' ? xamanState.txid : fiatState.phase === 'completed' ? fiatState.txid : undefined}
        transactionUrl={transactionUrl || undefined}
        isPublic={isPublic}
        onReturnHome={() => window.location.href = '/'}
      />
    );
  }

  return null;
};

export default WhisperFlow;
