import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Heart, MessageSquare, BarChart3, Settings, Users, Shield, ArrowRight, Globe, Zap, Eye, Wallet, CheckCircle2, Sparkles, Star, Gift, TrendingUp } from 'lucide-react';
import { getTotals } from '../services/api';
import './LandingPage.css';
import { useWallet } from '../context/WalletContext';
import IntentGiveCard from './common/IntentGiveCard';

interface DonationStats {
  MEDA?: number;
  TARA?: number;
}

interface LandingPageProps {
  userRole: 'donor' | 'charity' | 'admin';
  setUserRole: (role: 'donor' | 'charity' | 'admin') => void;
}

const LandingPage: React.FC<LandingPageProps> = ({ userRole, setUserRole }) => {
  const [totals, setTotals] = useState<DonationStats>({});
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const wallet = useWallet();

  useEffect(() => {
    const fetchTotals = async () => {
      try {
        const data = await getTotals();
        setTotals(data);
      } catch (error) {
        console.error('Failed to fetch totals:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchTotals();
  }, []);

  const total = (totals.MEDA || 0) + (totals.TARA || 0);

  const handleRoleChange = (newRole: 'donor' | 'charity' | 'admin') => {
    setUserRole(newRole);
  };

  // If not donor role, show role switcher and basic info
  if (userRole !== 'donor') {
    return (
      <div style={{ 
        minHeight: '100vh',
        background: '#ffffff',
        padding: '60px 20px'
      }}>
        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          textAlign: 'center'
        }}>
          <div style={{
            background: 'linear-gradient(135deg, #f8fafc 0%, #f1f5f9 100%)',
            padding: '60px 40px',
            borderRadius: '24px',
            marginBottom: '60px',
            border: '1px solid #e2e8f0'
          }}>
            <h1 style={{
              fontSize: '3.5rem',
              fontWeight: 900,
              color: '#0f172a',
              marginBottom: '20px'
            }}>
              Welcome to Eunoia Atlas
            </h1>
            <p style={{
              fontSize: '1.25rem',
              color: '#64748b',
              marginBottom: '50px',
              maxWidth: '600px',
              marginLeft: 'auto',
              marginRight: 'auto',
              lineHeight: '1.6'
            }}>
              Privacy-preserving charitable giving platform
            </p>

            <div style={{
              display: 'flex',
              justifyContent: 'center',
              gap: '20px',
              marginBottom: '50px',
              flexWrap: 'wrap'
            }}>
              <button 
                onClick={() => handleRoleChange('donor')}
                style={{
                  padding: '16px 32px',
                  borderRadius: '16px',
                  border: '2px solid #7c3aed',
                  background: 'transparent',
                  color: '#7c3aed',
                  fontWeight: 600,
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  fontSize: '1.1rem'
                }}
              >
                👤 Donor
              </button>
              <button 
                onClick={() => handleRoleChange('charity')}
                style={{
                  padding: '16px 32px',
                  borderRadius: '16px',
                  border: '2px solid #7c3aed',
                  background: userRole === 'charity' ? '#7c3aed' : 'transparent',
                  color: userRole === 'charity' ? '#fff' : '#7c3aed',
                  fontWeight: 600,
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  fontSize: '1.1rem'
                }}
              >
                🏢 Charity
              </button>
              <button 
                onClick={() => handleRoleChange('admin')}
                style={{
                  padding: '16px 32px',
                  borderRadius: '16px',
                  border: '2px solid #7c3aed',
                  background: userRole === 'admin' ? '#7c3aed' : 'transparent',
                  color: userRole === 'admin' ? '#fff' : '#7c3aed',
                  fontWeight: 600,
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  fontSize: '1.1rem'
                }}
              >
                ⚙️ Admin
              </button>
            </div>

            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
              gap: '30px',
              maxWidth: '900px',
              margin: '0 auto'
            }}>
              {userRole === 'charity' && (
                <>
                  <button
                    onClick={() => navigate('/analytics')}
                    style={{
                      background: '#fff',
                      border: '1px solid #e2e8f0',
                      borderRadius: '20px',
                      padding: '32px 24px',
                      textAlign: 'left',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease',
                      boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
                      display: 'block',
                      width: '100%'
                    }}
                  >
                    <BarChart3 size={40} style={{ color: '#7c3aed', marginBottom: '16px' }} />
                    <h3 style={{ margin: '0 0 12px 0', color: '#0f172a', fontSize: '1.4rem', fontWeight: '700' }}>View Analytics</h3>
                    <p style={{ margin: 0, color: '#64748b', fontSize: '1rem', lineHeight: '1.5' }}>Insights and donor engagement metrics</p>
                  </button>
                  <button
                    onClick={() => navigate('/charity')}
                    style={{
                      background: '#fff',
                      border: '1px solid #e2e8f0',
                      borderRadius: '20px',
                      padding: '32px 24px',
                      textAlign: 'left',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease',
                      boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
                      display: 'block',
                      width: '100%'
                    }}
                  >
                    <Users size={40} style={{ color: '#7c3aed', marginBottom: '16px' }} />
                    <h3 style={{ margin: '0 0 12px 0', color: '#0f172a', fontSize: '1.4rem', fontWeight: '700' }}>Charity Dashboard</h3>
                    <p style={{ margin: 0, color: '#64748b', fontSize: '1rem', lineHeight: '1.5' }}>Manage your organization's presence</p>
                  </button>
                </>
              )}

              {userRole === 'admin' && (
                <>
                  <button
                    onClick={() => navigate('/admin')}
                    style={{
                      background: '#fff',
                      border: '1px solid #e2e8f0',
                      borderRadius: '20px',
                      padding: '32px 24px',
                      textAlign: 'left',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease',
                      boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
                      display: 'block',
                      width: '100%'
                    }}
                  >
                    <Settings size={40} style={{ color: '#7c3aed', marginBottom: '16px' }} />
                    <h3 style={{ margin: '0 0 12px 0', color: '#0f172a', fontSize: '1.4rem', fontWeight: '700' }}>Platform Management</h3>
                    <p style={{ margin: 0, color: '#64748b', fontSize: '1rem', lineHeight: '1.5' }}>Oversee platform operations</p>
                  </button>
                  <button
                    onClick={() => navigate('/dashboard')}
                    style={{
                      background: '#fff',
                      border: '1px solid #e2e8f0',
                      borderRadius: '20px',
                      padding: '32px 24px',
                      textAlign: 'left',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease',
                      boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
                      display: 'block',
                      width: '100%'
                    }}
                  >
                    <BarChart3 size={40} style={{ color: '#7c3aed', marginBottom: '16px' }} />
                    <h3 style={{ margin: '0 0 12px 0', color: '#0f172a', fontSize: '1.4rem', fontWeight: '700' }}>System Dashboard</h3>
                    <p style={{ margin: 0, color: '#64748b', fontSize: '1rem', lineHeight: '1.5' }}>Monitor platform health</p>
                  </button>
                </>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '100vh',
        fontSize: '1.25rem',
        color: '#64748b',
        background: '#ffffff'
      }}>
        <div style={{
          textAlign: 'center',
          padding: '40px'
        }}>
          <div style={{
            width: '50px',
            height: '50px',
            border: '3px solid #f3f4f6',
            borderTop: '3px solid #7c3aed',
            borderRadius: '50%',
            animation: 'spin 1s linear infinite',
            margin: '0 auto 20px auto'
          }} />
          Loading Eunoia Atlas...
        </div>
      </div>
    );
  }

  return (
    <div style={{ 
      minHeight: '100vh',
      background: '#ffffff',
      padding: '0'
    }}>
      {/* Hero Section */}
      <div style={{
        background: 'linear-gradient(135deg, #f8fafc 0%, #f1f5f9 100%)',
        padding: '100px 20px 80px 20px',
        textAlign: 'center',
        color: '#0f172a',
        position: 'relative',
        overflow: 'hidden',
        borderBottom: '1px solid #e2e8f0'
      }}>
        {/* Background Pattern */}
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'radial-gradient(circle at 20% 80%, rgba(124, 58, 237, 0.03) 0%, transparent 50%), radial-gradient(circle at 80% 20%, rgba(124, 58, 237, 0.03) 0%, transparent 50%)',
          pointerEvents: 'none'
        }} />

        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          position: 'relative',
          zIndex: 1
        }}>
          <div style={{
            background: 'rgba(124, 58, 237, 0.05)',
            color: '#7c3aed',
            padding: '8px 16px',
            borderRadius: '20px',
            display: 'inline-block',
            marginBottom: '30px',
            fontSize: '0.9rem',
            fontWeight: '600',
            border: '1px solid rgba(124, 58, 237, 0.1)'
          }}>
            ✨ Privacy-Preserving Platform
          </div>

          <h1 style={{
            fontSize: '4.5rem',
            fontWeight: 900,
            marginBottom: '30px',
            lineHeight: 1.1,
            color: '#0f172a'
          }}>
            Give with
            <span style={{
              background: 'linear-gradient(135deg, #7c3aed, #a855f7)',
              WebkitBackgroundClip: 'text',
              WebkitTextFillColor: 'transparent',
              backgroundClip: 'text'
            }}> Impact</span>
          </h1>
          <p style={{
            fontSize: '1.5rem',
            marginBottom: '50px',
            color: '#64748b',
            maxWidth: '700px',
            marginLeft: 'auto',
            marginRight: 'auto',
            lineHeight: '1.6',
            fontWeight: '400'
          }}>
            Connect your intent with real-world impact through privacy-preserving charitable giving
          </p>

          {/* Role Switcher */}
          <div style={{
            display: 'flex',
            justifyContent: 'center',
            gap: '20px',
            marginBottom: '60px',
            flexWrap: 'wrap'
          }}>
            <button 
              onClick={() => handleRoleChange('donor')}
              style={{
                padding: '18px 36px',
                borderRadius: '18px',
                border: '2px solid #7c3aed',
                background: 'rgba(124, 58, 237, 0.1)',
                color: '#7c3aed',
                fontWeight: '600',
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                fontSize: '1.1rem',
