import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Heart, MessageSquare, BarChart3, Settings, Users, Shield, ArrowRight, Globe, Zap, Eye, Wallet, CheckCircle2 } from 'lucide-react';
import { getTotals } from '../services/api';
import './LandingPage.css';
import { useWallet } from '../context/WalletContext';
import IntentGiveCard from './common/IntentGiveCard';

interface DonationStats {
  MEDA?: number;
  TARA?: number;
}

interface LandingPageProps {
  userRole: 'donor' | 'charity' | 'admin';
  setUserRole: (role: 'donor' | 'charity' | 'admin') => void;
}

const LandingPage: React.FC<LandingPageProps> = ({ userRole, setUserRole }) => {
  const [totals, setTotals] = useState<DonationStats>({});
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const wallet = useWallet();

  useEffect(() => {
    const fetchTotals = async () => {
      try {
        const data = await getTotals();
        setTotals(data);
      } catch (error) {
        console.error('Failed to fetch totals:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchTotals();
  }, []);

  const total = (totals.MEDA || 0) + (totals.TARA || 0);

  const handleRoleChange = (newRole: 'donor' | 'charity' | 'admin') => {
    setUserRole(newRole);
  };

  // If not donor role, show role switcher and basic info
  if (userRole !== 'donor') {
    return (
      <div style={{ 
        minHeight: '100vh',
        background: 'linear-gradient(135deg, #f8fafc 0%, #f1f5f9 100%)',
        padding: '40px 20px'
      }}>
        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          textAlign: 'center'
        }}>
          <h1 style={{
            fontSize: '3rem',
            fontWeight: 900,
            color: '#0f172a',
            marginBottom: '16px'
          }}>
            Welcome to Eunoia Atlas
          </h1>
          <p style={{
            fontSize: '1.25rem',
            color: '#64748b',
            marginBottom: '40px',
            maxWidth: '600px',
            marginLeft: 'auto',
            marginRight: 'auto'
          }}>
            Privacy-preserving charitable giving platform
          </p>

          <div style={{
            display: 'flex',
            justifyContent: 'center',
            gap: '16px',
            marginBottom: '40px'
          }}>
            <button 
              onClick={() => handleRoleChange('donor')}
              style={{
                padding: '12px 24px',
                borderRadius: '12px',
                border: '2px solid #7c3aed',
                background: 'transparent',
                color: '#7c3aed',
                fontWeight: 600,
                cursor: 'pointer',
                transition: 'all 0.2s ease'
              }}
            >
              üë§ Donor
            </button>
            <button 
              onClick={() => handleRoleChange('charity')}
              style={{
                padding: '12px 24px',
                borderRadius: '12px',
                border: '2px solid #7c3aed',
                background: userRole === 'charity' ? '#7c3aed' : 'transparent',
                color: userRole === 'charity' ? '#fff' : '#7c3aed',
                fontWeight: 600,
                cursor: 'pointer',
                transition: 'all 0.2s ease'
              }}
            >
              üè¢ Charity
            </button>
            <button 
              onClick={() => handleRoleChange('admin')}
              style={{
                padding: '12px 24px',
                borderRadius: '12px',
                border: '2px solid #7c3aed',
                background: userRole === 'admin' ? '#7c3aed' : 'transparent',
                color: userRole === 'admin' ? '#fff' : '#7c3aed',
                fontWeight: 600,
                cursor: 'pointer',
                transition: 'all 0.2s ease'
              }}
            >
              ‚öôÔ∏è Admin
            </button>
          </div>

          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
            gap: '24px',
            maxWidth: '800px',
            margin: '0 auto'
          }}>
            {userRole === 'charity' && (
              <>
                <button
                  onClick={() => navigate('/analytics')}
                  style={{
                    background: '#fff',
                    border: '1px solid #e2e8f0',
                    borderRadius: '16px',
                    padding: '24px',
                    textAlign: 'left',
                    cursor: 'pointer',
                    transition: 'all 0.2s ease',
                    boxShadow: '0 1px 3px rgba(0,0,0,.1)'
                  }}
                >
                  <BarChart3 size={32} style={{ color: '#7c3aed', marginBottom: '12px' }} />
                  <h3 style={{ margin: '0 0 8px 0', color: '#0f172a' }}>View Analytics</h3>
                  <p style={{ margin: 0, color: '#64748b' }}>Insights and donor engagement metrics</p>
                </button>
                <button
                  onClick={() => navigate('/charity')}
                  style={{
                    background: '#fff',
                    border: '1px solid #e2e8f0',
                    borderRadius: '16px',
                    padding: '24px',
                    textAlign: 'left',
                    cursor: 'pointer',
                    transition: 'all 0.2s ease',
                    boxShadow: '0 1px 3px rgba(0,0,0,.1)'
                  }}
                >
                  <Users size={32} style={{ color: '#7c3aed', marginBottom: '12px' }} />
                  <h3 style={{ margin: '0 0 8px 0', color: '#0f172a' }}>Charity Dashboard</h3>
                  <p style={{ margin: 0, color: '#64748b' }}>Manage your organization's presence</p>
                </button>
              </>
            )}

            {userRole === 'admin' && (
              <>
                <button
                  onClick={() => navigate('/admin')}
                  style={{
                    background: '#fff',
                    border: '1px solid #e2e8f0',
                    borderRadius: '16px',
                    padding: '24px',
                    textAlign: 'left',
                    cursor: 'pointer',
                    transition: 'all 0.2s ease',
                    boxShadow: '0 1px 3px rgba(0,0,0,.1)'
                  }}
                >
                  <Settings size={32} style={{ color: '#7c3aed', marginBottom: '12px' }} />
                  <h3 style={{ margin: '0 0 8px 0', color: '#0f172a' }}>Platform Management</h3>
                  <p style={{ margin: 0, color: '#64748b' }}>Oversee platform operations</p>
                </button>
                <button
                  onClick={() => navigate('/dashboard')}
                  style={{
                    background: '#fff',
                    border: '1px solid #e2e8f0',
                    borderRadius: '16px',
                    padding: '24px',
                    textAlign: 'left',
                    cursor: 'pointer',
                    transition: 'all 0.2s ease',
                    boxShadow: '0 1px 3px rgba(0,0,0,.1)'
                  }}
                >
                  <BarChart3 size={32} style={{ color: '#7c3aed', marginBottom: '12px' }} />
                  <h3 style={{ margin: '0 0 8px 0', color: '#0f172a' }}>System Dashboard</h3>
                  <p style={{ margin: 0, color: '#64748b' }}>Monitor platform health</p>
                </button>
              </>
            )}
          </div>
        </div>
      </div>
    );
  }

  if (loading) {
    return (
      <div style={{
        display: 'flex',
        justifyContent: 'center',
        alignItems: 'center',
        minHeight: '400px',
        fontSize: '1.125rem',
        color: '#64748b'
      }}>
        Loading Eunoia Atlas...
      </div>
    );
  }

  return (
    <div style={{ 
      minHeight: '100vh',
      background: 'linear-gradient(135deg, #f8fafc 0%, #f1f5f9 100%)',
      padding: '0'
    }}>
      {/* Hero Section */}
      <div style={{
        background: 'linear-gradient(135deg, #7c3aed 0%, #6d28d9 100%)',
        padding: '80px 20px',
        textAlign: 'center',
        color: '#fff',
        position: 'relative',
        overflow: 'hidden'
      }}>
        {/* Background Pattern */}
        <div style={{
          position: 'absolute',
          top: 0,
          left: 0,
          right: 0,
          bottom: 0,
          background: 'radial-gradient(circle at 20% 80%, rgba(255,255,255,0.1) 0%, transparent 50%), radial-gradient(circle at 80% 20%, rgba(255,255,255,0.1) 0%, transparent 50%)',
          pointerEvents: 'none'
        }} />

        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          position: 'relative',
          zIndex: 1
        }}>
          <h1 style={{
            fontSize: '4rem',
            fontWeight: 900,
            marginBottom: '24px',
            lineHeight: 1.1
          }}>
            Give with Impact
          </h1>
          <p style={{
            fontSize: '1.5rem',
            marginBottom: '40px',
            opacity: 0.9,
            maxWidth: '600px',
            marginLeft: 'auto',
            marginRight: 'auto',
            lineHeight: 1.6
          }}>
            Privacy-preserving charitable giving that connects your intent with real-world impact
          </p>

          {/* Role Switcher */}
          <div style={{
            display: 'flex',
            justifyContent: 'center',
            gap: '16px',
            marginBottom: '40px',
            flexWrap: 'wrap'
          }}>
            <button 
              onClick={() => handleRoleChange('donor')}
              style={{
                padding: '16px 32px',
                borderRadius: '16px',
                border: '2px solid rgba(255,255,255,0.3)',
                background: 'rgba(255,255,255,0.2)',
                color: '#fff',
                fontWeight: 600,
                cursor: 'pointer',
                transition: 'all 0.3s ease',
                backdropFilter: 'blur(10px)'
              }}
            >
              üë§ Donor
            </button>
            <button 
              onClick={() => handleRoleChange('charity')}
              style={{
                padding: '16px 32px',
                borderRadius: '16px',
                border: '2px solid rgba(255,255,255,0.3)',
                background: 'transparent',
                color: '#fff',
                fontWeight: 600,
                cursor: 'pointer',
                transition: 'all 0.3s ease'
              }}
            >
              üè¢ Charity
            </button>
            <button 
              onClick={() => handleRoleChange('admin')}
              style={{
                padding: '16px 32px',
                borderRadius: '16px',
                border: '2px solid rgba(255,255,255,0.3)',
                background: 'transparent',
                color: '#fff',
                fontWeight: 600,
                cursor: 'pointer',
                transition: 'all 0.3s ease'
              }}
            >
              ‚öôÔ∏è Admin
            </button>
          </div>
        </div>
      </div>

      {/* Main Content */}
      <div style={{
        maxWidth: '1200px',
        margin: '0 auto',
        padding: '60px 20px'
      }}>
        {/* Main Donation Flow */}
        <div style={{ marginBottom: '80px' }}>
          <IntentGiveCard />
        </div>

        {/* Impact Stats */}
        <div style={{ marginBottom: '80px' }}>
          <h2 style={{
            textAlign: 'center',
            marginBottom: '48px',
            color: '#0f172a',
            fontSize: '2.5rem',
            fontWeight: 800
          }}>
            Platform Impact
          </h2>
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(250px, 1fr))',
            gap: '32px'
          }}>
            <div style={{
              background: 'linear-gradient(135deg, #fff 0%, #f8fafc 100%)',
              padding: '40px 32px',
              borderRadius: '24px',
              border: '1px solid #e2e8f0',
              textAlign: 'center',
              boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
              transition: 'transform 0.3s ease'
            }}>
              <div style={{
                fontSize: '3rem',
                fontWeight: 900,
                color: '#7c3aed',
                marginBottom: '16px'
              }}>
                ${total.toLocaleString()}
              </div>
              <div style={{
                color: '#64748b',
                fontWeight: 600,
                fontSize: '1.125rem'
              }}>
                Total Donations
              </div>
            </div>
            <div style={{
              background: 'linear-gradient(135deg, #fff 0%, #f8fafc 100%)',
              padding: '40px 32px',
              borderRadius: '24px',
              border: '1px solid #e2e8f0',
              textAlign: 'center',
              boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
              transition: 'transform 0.3s ease'
            }}>
              <div style={{
                fontSize: '3rem',
                fontWeight: 900,
                color: '#7c3aed',
                marginBottom: '16px'
              }}>
                2
              </div>
              <div style={{
                color: '#64748b',
                fontWeight: 600,
                fontSize: '1.125rem'
              }}>
                Partner Charities
              </div>
            </div>
            <div style={{
              background: 'linear-gradient(135deg, #fff 0%, #f8fafc 100%)',
              padding: '40px 32px',
              borderRadius: '24px',
              border: '1px solid #e2e8f0',
              textAlign: 'center',
              boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
              transition: 'transform 0.3s ease'
            }}>
              <div style={{
                fontSize: '3rem',
                fontWeight: 900,
                color: '#7c3aed',
                marginBottom: '16px'
              }}>
                100%
              </div>
              <div style={{
                color: '#64748b',
                fontWeight: 600,
                fontSize: '1.125rem'
              }}>
                Privacy Protected
              </div>
            </div>
            <div style={{
              background: 'linear-gradient(135deg, #fff 0%, #f8fafc 100%)',
              padding: '40px 32px',
              borderRadius: '24px',
              border: '1px solid #e2e8f0',
              textAlign: 'center',
              boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
              transition: 'transform 0.3s ease'
            }}>
              <div style={{
                fontSize: '3rem',
                fontWeight: 900,
                color: '#7c3aed',
                marginBottom: '16px'
              }}>
                Real-time
              </div>
              <div style={{
                color: '#64748b',
                fontWeight: 600,
                fontSize: '1.125rem'
              }}>
                Blockchain Verified
              </div>
            </div>
          </div>
        </div>

        {/* Partner Charities */}
        <div style={{ marginBottom: '80px' }}>
          <h2 style={{
            textAlign: 'center',
            marginBottom: '48px',
            color: '#0f172a',
            fontSize: '2.5rem',
            fontWeight: 800
          }}>
            Our Partner Charities
          </h2>
          <div style={{
            display: 'grid',
            gridTemplateColumns: 'repeat(auto-fit, minmax(400px, 1fr))',
            gap: '32px'
          }}>
            <div style={{
              background: 'linear-gradient(135deg, #fff 0%, #f8fafc 100%)',
              padding: '48px 40px',
              borderRadius: '24px',
              border: '1px solid #e2e8f0',
              textAlign: 'center',
              boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
              transition: 'transform 0.3s ease'
            }}>
              <h3 style={{
                color: '#0f172a',
                fontSize: '2rem',
                marginBottom: '16px',
                fontWeight: 700
              }}>
                MEDA
              </h3>
              <p style={{
                color: '#64748b',
                marginBottom: '24px',
                lineHeight: 1.6,
                fontSize: '1.125rem'
              }}>
                Mennonite Economic Development Associates
              </p>
              <div style={{
                background: 'linear-gradient(135deg, #7c3aed 0%, #6d28d9 100%)',
                padding: '20px',
                borderRadius: '16px',
                fontWeight: 700,
                color: '#fff',
                fontSize: '1.25rem'
              }}>
                Total: ${(totals.MEDA || 0).toLocaleString()}
              </div>
            </div>
            <div style={{
              background: 'linear-gradient(135deg, #fff 0%, #f8fafc 100%)',
              padding: '48px 40px',
              borderRadius: '24px',
              border: '1px solid #e2e8f0',
              textAlign: 'center',
              boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
              transition: 'transform 0.3s ease'
            }}>
              <h3 style={{
                color: '#0f172a',
                fontSize: '2rem',
                marginBottom: '16px',
                fontWeight: 700
              }}>
                TARA
              </h3>
              <p style={{
                color: '#64748b',
                marginBottom: '24px',
                lineHeight: 1.6,
                fontSize: '1.125rem'
              }}>
                Technology for Rural Advancement
              </p>
              <div style={{
                background: 'linear-gradient(135deg, #7c3aed 0%, #6d28d9 100%)',
                padding: '20px',
                borderRadius: '16px',
                fontWeight: 700,
                color: '#fff',
                fontSize: '1.25rem'
              }}>
                Total: ${(totals.TARA || 0).toLocaleString()}
              </div>
            </div>
          </div>
        </div>

        {/* Additional Actions */}
        <div style={{
          background: 'linear-gradient(135deg, #fff 0%, #f8fafc 100%)',
          padding: '60px 40px',
          borderRadius: '32px',
          border: '1px solid #e2e8f0',
          textAlign: 'center',
          boxShadow: '0 4px 20px rgba(0,0,0,0.08)'
        }}>
          <h2 style={{
            color: '#0f172a',
            marginBottom: '20px',
            fontSize: '2.5rem',
            fontWeight: 800
          }}>
            More Ways to Give
          </h2>
