import React from 'react';

interface QRModalProps {
  open: boolean;
  title: string;
  qrUrl?: string;
  openLink?: string;
  network?: string;
  status?: string;
  onClose: () => void;
}

const QRModal: React.FC<QRModalProps> = ({ open, title, qrUrl, openLink, network, status, onClose }) => {
  if (!open) return null;
  return (
    <div style={{ position: 'fixed', inset: 0, background: 'rgba(15,23,42,.5)', display: 'grid', placeItems: 'center', zIndex: 50 }}>
      <div style={{ background: '#fff', borderRadius: 12, padding: 16, width: 'min(92vw, 420px)', boxShadow: '0 20px 60px rgba(0,0,0,.3)' }}>
        <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center', marginBottom: 8 }}>
          <h3 style={{ margin: 0, fontSize: 18, color: '#1e293b' }}>{title}</h3>
          <button onClick={onClose} style={{ background: 'transparent', border: 'none', fontSize: 18, cursor: 'pointer' }}>Ã—</button>
        </div>
        {network && (
          <div style={{ fontSize: 12, color: '#334155', background: '#f1f5f9', display: 'inline-flex', padding: '4px 8px', borderRadius: 999, border: '1px solid #e2e8f0' }}>Network: {network}</div>
        )}
        <div style={{ display: 'grid', placeItems: 'center', marginTop: 12 }}>
          {qrUrl ? (
            <img src={qrUrl} alt="Xaman QR" style={{ width: 240, height: 240, borderRadius: 8 }} />
          ) : openLink ? (
            <a href={openLink} target="_blank" rel="noreferrer">Open in Xaman</a>
          ) : null}
        </div>
        {status && <div style={{ marginTop: 8, color: '#475569', fontSize: 14 }}>{status}</div>}
      </div>
    </div>
  );
};

export default QRModal;
