import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Heart, MessageSquare, BarChart3, Settings, Users, Shield, ArrowRight, Globe, Zap, Eye, Wallet, CheckCircle2, Sparkles, Star, Gift, TrendingUp, ArrowDown, ChevronRight, Lock, Users2, Globe2, Award } from 'lucide-react';
import { getTotals } from '../services/api';
import './LandingPage.css';
import { useWallet } from '../context/WalletContext';

interface DonationStats {
  MEDA?: number;
  TARA?: number;
}

interface LandingPageProps {
  userRole: 'donor' | 'charity' | 'admin';
  setUserRole: (role: 'donor' | 'charity' | 'admin') => void;
}

const LandingPage: React.FC<LandingPageProps> = ({ userRole, setUserRole }) => {
  const [totals, setTotals] = useState<DonationStats>({});
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();
  const wallet = useWallet();

  useEffect(() => {
    const fetchTotals = async () => {
      try {
        const data = await getTotals();
        setTotals(data);
      } catch (error) {
        console.error('Failed to fetch totals:', error);
      } finally {
        setLoading(false);
      }
    };

    fetchTotals();
  }, []);

  const total = (totals.MEDA || 0) + (totals.TARA || 0);

  const handleRoleChange = (newRole: 'donor' | 'charity' | 'admin') => {
    setUserRole(newRole);
  };

  // If not donor role, show role switcher and basic info
  if (userRole !== 'donor') {
    return (
      <div style={{ 
        minHeight: '100vh',
        background: '#ffffff',
        padding: '60px 20px'
      }}>
        <div style={{
          maxWidth: '1200px',
          margin: '0 auto',
          textAlign: 'center'
        }}>
          <div style={{
            background: 'linear-gradient(135deg, #f8fafc 0%, #f1f5f9 100%)',
            padding: '60px 40px',
            borderRadius: '24px',
            marginBottom: '60px',
            border: '1px solid #e2e8f0'
          }}>
            <h1 style={{
              fontSize: '3.5rem',
              fontWeight: 900,
              color: '#0f172a',
              marginBottom: '20px'
            }}>
              Welcome to Eunoia Atlas
            </h1>
            <p style={{
              fontSize: '1.25rem',
              color: '#64748b',
              marginBottom: '50px',
              maxWidth: '600px',
              marginLeft: 'auto',
              marginRight: 'auto',
              lineHeight: '1.6'
            }}>
              Privacy-preserving charitable giving platform
            </p>

            <div style={{
              display: 'flex',
              justifyContent: 'center',
              gap: '20px',
              marginBottom: '50px',
              flexWrap: 'wrap'
            }}>
              <button 
                onClick={() => handleRoleChange('donor')}
                style={{
                  padding: '16px 32px',
                  borderRadius: '16px',
                  border: '2px solid #7c3aed',
                  background: 'transparent',
                  color: '#7c3aed',
                  fontWeight: 600,
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  fontSize: '1.1rem'
                }}
              >
                👤 Donor
              </button>
              <button 
                onClick={() => handleRoleChange('charity')}
                style={{
                  padding: '16px 32px',
                  borderRadius: '16px',
                  border: '2px solid #7c3aed',
                  background: userRole === 'charity' ? '#7c3aed' : 'transparent',
                  color: userRole === 'charity' ? '#fff' : '#7c3aed',
                  fontWeight: 600,
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  fontSize: '1.1rem'
                }}
              >
                🏢 Charity
              </button>
              <button 
                onClick={() => handleRoleChange('admin')}
                style={{
                  padding: '16px 32px',
                  borderRadius: '16px',
                  border: '2px solid #7c3aed',
                  background: userRole === 'admin' ? '#7c3aed' : 'transparent',
                  color: userRole === 'admin' ? '#fff' : '#7c3aed',
                  fontWeight: 600,
                  cursor: 'pointer',
                  transition: 'all 0.3s ease',
                  fontSize: '1.1rem'
                }}
              >
                ⚙️ Admin
              </button>
            </div>

            <div style={{
              display: 'grid',
              gridTemplateColumns: 'repeat(auto-fit, minmax(300px, 1fr))',
              gap: '30px',
              maxWidth: '900px',
              margin: '0 auto'
            }}>
              {userRole === 'charity' && (
                <>
                  <button
                    onClick={() => navigate('/analytics')}
                    style={{
                      background: '#fff',
                      border: '1px solid #e2e8f0',
                      borderRadius: '20px',
                      padding: '32px 24px',
                      textAlign: 'left',
                      cursor: 'pointer',
                      transition: 'all 0.3s ease',
                      boxShadow: '0 4px 20px rgba(0,0,0,0.08)',
                      display: 'block',
                      width: '100%'
                    }}
                  >
                    <BarChart3 size={40} style={{ color: '#7c3aed', marginBottom: '16px' }} />
